// 车钻石初始消费价格
// 1、一级车辆单个钻石定价为：36
// 2、车辆成本递增为上一级的1.3，购买次数递增为上一级的一点1.18，车（钻石价）=36*1.3^（n-1）
var carBaseDiamondPrice = 36;
//获取汽车配置
var carReord = {};
var getCarConfig = function (_id) {
    if (carReord[_id]) {
        return carReord[_id]; //查询优化
    }
    for (var key in CarConfig) {
        if (CarConfig.hasOwnProperty(key)) {
            var element = CarConfig[key];
            if (element.id == _id) {
                carReord[_id] = element;
                return element;
            }
        }
    }
    return null;
};
//获取最新开锁(可购买)汽车配置
var getNewCarConfig = function (_unlockCarId) {
    var carCfg = null;
    for (var key in CarConfig) {
        if (CarConfig.hasOwnProperty(key)) {
            var element = CarConfig[key];
            if (_unlockCarId < element.UnlockNeedId) {
                break;
            }
            carCfg = element;
        }
    }
    return carCfg;
};
//获取最新开锁(可购买)的前n辆汽车配置
var getPreNewCarConfig = function (_unlockCarId, _index) {
    var carCfg = null;
    for (var key in CarConfig) {
        if (CarConfig.hasOwnProperty(key)) {
            var element = CarConfig[key];
            if (_unlockCarId < element.UnlockNeedId) {
                carCfg = getCarConfig(parseInt(key) + _index);
                break;
            }
        }
    }
    return carCfg;
};
//获取汽车价格
var getCarPrice = function (_price, _buyTimes) {
    if (_buyTimes > 0) {
        // return _price * Math.pow(1.075, _buyTimes);
        return _price * Math.pow(1.175, _buyTimes);
    }
    return _price;
};
//获取汽车钻石价格
var getCarDiamondPrice = function (_carId, _buyTimes) {
    if (_carId < 1) {
        return carBaseDiamondPrice;
    }
    var carPrice = carBaseDiamondPrice * Math.pow(1.3, _carId - 1);
    if (_buyTimes > 0) {
        return carPrice * Math.pow(1.18, _buyTimes);
    }
    return carPrice;
};
var getCarMaxLevel = function () {
    return CarConfig.length;
};
